INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (1, 'Iphone 4', 3100, 5299, 'The iPhone 4 is a smartphone that was designed and marketed by Apple Inc. It is the fourth generation of the iPhone lineup, succeeding the iPhone 3GS and preceding the 4S. the iPhone 4 was first unveiled on June 7, 2010.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (2, 'Iphone 4s', 800, 5299, 'The iPhone 4s is a smartphone that was designed and marketed by Apple Inc. It is the fifth generation of the iPhone, succeeding the iPhone 4 and preceding the iPhone 5. It was announced on October 4, 2011.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (3, 'Nokia Lumia 920', 200, 5499, 'Nokia Lumia 920 is a smartphone developed by Nokia that runs the Windows Phone 8 operating system. It was announced on September 5, 2012. It has a 1.5 GHz dual-core Qualcomm Krait CPU and a 4.5" IPS TFT LCD display.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (4, 'iPhone 5', 1300, 4999, 'The iPhone 5 is a smartphone that was designed and marketed by Apple Inc. It is the 6th generation iPhone, succeeding the iPhone 4S and preceding both the iPhone 5S and 5C.');
