INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (6, 'Samsung Galaxy 3', 1500, 5299, 'The Samsung Galaxy 3, also known as the Samsung Galaxy Apollo, Galaxy Mini in Italy, or Samsung Galaxy 580 in Hong Kong, is a smartphone manufactured by Samsung that runs the open source Android operating system.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (7, 'Samsung Galaxy Note II', 2300, 6299, 'The Samsung Galaxy Note II is an Android phablet smartphone. Unveiled on August 29, 2012 and released in October 2012, the Galaxy Note II is a successor to the original Galaxy Note, a larger 5.5-inch screen.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (8, 'Sony Xperia Z2', 2100, 4899, 'The Sony Xperia Z2 is an Android-based smartphone unveiled, manufactured, and marketed by Sony and was released in April 2014. the Xperia Z2 is water and dust proof with an IP rating of IP55 and IP58.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (9, 'Nokia Lumia 1020', 1800, 5299, 'The Nokia Lumia 1020 is a smartphone developed by Nokia, first unveiled on 11 July 2013 at a Nokia event in New York. It contains Nokia PureView technology, It was considered to be the most advanced cameraphone when released in September 2013.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (10, 'Nokia Lumia 1520', 1600, 5699, 'The Nokia Lumia 1520 was a flagship Windows Phone phablet smartphone designed and produced by Finnish telecommunications manufacturer Nokia in partnership with American software manufacturer Microsoft.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (11, 'Meizu MX2', 900, 2299, 'The Meizu MX2 is a smartphone designed and produced by the Chinese manufacturer Meizu, It is a previous model of the MX series, succeeding the Meizu MX and preceding the Meizu MX3. It was unveiled on November 27, 2012 in Beijing.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (12, 'Moto X', 1400, 4259, 'Moto X is an Android smartphone developed and manufactured by Motorola Mobility, and released in August 2013.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (13, 'Moto X 2', 300, 5299, 'The second generation Moto X, marketed as moto X and referred to in the media as Moto X (2014), is an Android smartphone developed by Motorola Mobility. Released on September 5, 2014.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (14, 'Nexus 6', 300, 5299, 'The Nexus 6 is a phablet co-developed by Google and Motorola Mobility that runs the Android operating system. the device is the sixth smartphone in the Google Nexus series.');
INSERT INTO goods(`id`, `name`, `quantity`, `price`, `desc`) VALUES (15, 'BlackBerry 10', 300, 5299, 'BlackBerry 10 is a mobile operating system for the BlackBerry smartphones, BlackBerry 10 is based on QNX, a Unix-like operating system that was originally developed by QNX Software Systems until the company was acquired by BlackBerry in April 2010.');